import React from "react";

import './nav.css';

import { useState, useEffect } from "react";
import logo from './../../images/mainlogo.png';
import {useNavigate} from 'react-router-dom';
import NavButton from "./NavButton";
/**navigation bar */
const Navbar = () =>{

    const navigate = useNavigate();

    const [isMobile, setIsMobile] = useState(window.innerWidth <= 769); 

    const handleResize = () => {
      setIsMobile(window.innerWidth <= 769); 
    };

    const handleOnClick = () =>{
        navigate('/')
    }

    useEffect(() => {
      window.addEventListener("resize", handleResize);
      return () => {
        window.removeEventListener("resize", handleResize);
      };
    }, []);

    if(isMobile){
        return(
            <div className= "nav" onClick = {handleOnClick}>
                <img src = {logo} className="logo-image"/>
            </div>
        )
    }
    return (

        <div className= "nav">
            <div className="aligned-section">
                <img src ={logo} className="logo-image"/>
                <nav className="nav-button-container">
                    <NavButton style={'white-button'} text={'HOME'} dest={'/'}>
                    </NavButton>
                    <NavButton style={'white-button'} text={'ABOUT US'} dest={'/about'}>
                    </NavButton>
                    <NavButton style={'white-button'} text={'CONTACT US'} dest={'/contact'}>
                    </NavButton>
                    <NavButton style={'light-blue-button'} text={<b>Log in</b>}>
                    </NavButton>
                </nav>
            </div>
        </div>
    )
}

export default Navbar;
